import asyncio
from typing import Any
from unittest.mock import MagicMock, patch

import pytest
from app.core.faiss import FaissClient
from app.core.model_api import ModelAPIClient
from app.core.bing import BingSearch
from app.models.query import QueryResult
from requests_mock import Mocker
from tests.utils import async_mock_callable


class TestQuery:
    @pytest.fixture
    def query_result(self, query_document):
        return QueryResult(score=0, document=query_document, id=query_document.id)

    def test_search_bm25(self, client, datastore_name, query_document, query_result):
        response = client.get(
            "/datastores/{}/search".format(datastore_name),
            params={"query": "quack"},
        )
        assert response.status_code == 200
        assert response.json()[0]["document"] == query_result.document.__root__

    def test_search_dpr(
        self,
        requests_mock: Mocker,
        client,
        datastore_name,
        dpr_index,
        query_document,
        query_result,
    ):
        requests_mock.real_http = True
        requests_mock.post(
            f"{FaissClient.build_faiss_url(datastore_name, dpr_index.name)}/search",
            json=[
                {query_document["id"]: -5}
            ],  # use an impossible score to test that this return value is used
        )
        model_api_return = {
            "model_outputs": {
                "embeddings": ""
            }
        }

        with patch.object(
            ModelAPIClient, "predict", new_callable=async_mock_callable(model_api_return)
        ):
            response = client.get(
                "/datastores/{}/search".format(datastore_name),
                params={"index_name": "dpr", "query": "quack"},
            )
        assert response.status_code == 200
        assert response.json()[0]["document"] == query_result.document.__root__
        assert response.json()[0]["score"] == -5

    def test_search_not_found(self, client, datastore_name):
        response = client.get(
            "/datastores/{}/search".format(datastore_name),
            params={"query": "quack", "index_name": "unknown_index"},
        )
        assert response.status_code == 404
        assert "detail" in response.json()

    def test_search_by_vector(
        self,
        requests_mock: Mocker,
        client,
        datastore_name,
        dpr_index,
        query_document,
        query_result,
    ):
        requests_mock.real_http = True
        requests_mock.post(
            f"{FaissClient.build_faiss_url(datastore_name, dpr_index.name)}/search",
            json=[
                {query_document["id"]: -5}
            ],  # use an impossible score to test that this return value is used
        )

        response = client.post(
            "/datastores/{}/search_by_vector".format(datastore_name),
            json={"index_name": dpr_index.name, "query_vector": [0] * 768},
        )
        assert response.status_code == 200
        assert response.json()[0]["document"] == query_result.document.__root__
        assert response.json()[0]["score"] == -5

    def test_score(self, client, datastore_name, query_document, query_result):
        response = client.get(
            "/datastores/{}/score".format(datastore_name),
            params={"query": "quack", "doc_id": query_document["id"]},
        )
        assert response.status_code == 200
        assert "document" in response.json()
        assert response.json()["document"] == query_result.document.__root__

    def test_score_dpr(
        self,
        requests_mock: Mocker,
        client,
        datastore_name,
        dpr_index,
        query_document,
        query_result,
    ):
        requests_mock.real_http = True
        requests_mock.post(
            f"{FaissClient.build_faiss_url(datastore_name, dpr_index.name)}/explain",
            json={
                "score": -5
            },  # use an impossible score to test that this return value is used
        )

        model_api_return = {
            "model_outputs": {
                "embeddings": ""
            }
        }
        with patch.object(ModelAPIClient, "predict", new_callable=async_mock_callable(model_api_return)):
            response = client.get(
                "/datastores/{}/score".format(datastore_name),
                params={
                    "index_name": "dpr",
                    "query": "quack",
                    "doc_id": query_document["id"],
                },
            )

        assert response.status_code == 200
        assert response.json()["document"] == query_result.document.__root__
        assert response.json()["score"] == -5

    def test_score_not_found(self, client, datastore_name):
        response = client.get(
            "/datastores/{}/score".format(datastore_name),
            params={"query": "quack", "doc_id": "99999"},
        )
        assert response.status_code == 404
        assert "detail" in response.json()

    def test_unsupported_operation_for_bing_search(self, client, bing_search_datastore_name):
        response = client.get(
            f"/datastores/{bing_search_datastore_name}/search_by_vector",
        )
        assert response.status_code == 404

        response = client.get(
            f"/datastores/{bing_search_datastore_name}/score",
        )
        assert response.status_code == 404


    def test_bing_search_available(self,requests_mock: Mocker,
                         client,
                         bing_search_datastore_name,
                         query_result,

    ):
        '''
        Test if the bing search api is up


        '''

        bing_search_return = [query_result]
        with patch.object(BingSearch, "search", new_callable=async_mock_callable(bing_search_return)):
            response = client.get(
                "/datastores/{}/search".format(bing_search_datastore_name),
                    params={
                        "query": "who is the president of US?",
                        "top_k":1
                    },
            )
        assert len(response.json()) == 1
        assert response.json()[0]["document"] == query_result.document.__root__
        assert response.json()[0]["score"] ==0
        assert response.json()[0]["id"] == "222"
        assert response.status_code == 200

    def test_bing_search_max_count(self,requests_mock: Mocker,
                         client,
                         bing_search_datastore_name,
                         query_result,

    ):
        '''
        Test if bing search api could have correct length of return


        '''
        
        bing_search_return = [query_result for i in range(50) ]
        with patch.object(BingSearch, "search", new_callable=async_mock_callable(bing_search_return)):
            response = client.get(
                "/datastores/{}/search".format(bing_search_datastore_name),
                    params={
                        "query": "who is the president of US?",
                        "top_k":50
                    },
            )

        assert response.status_code == 200
        assert len(response.json()) <= 50

